create table admin (id bigint not null auto_increment, address varchar(255), city varchar(255), country varchar(255), createdAt datetime, dateOfBirth date, email varchar(255) not null unique, firstName varchar(30) not null, gender varchar(255), handphoneNo varchar(255), lastName varchar(30) not null, middleName varchar(10) not null, password varchar(255), secretAnswer varchar(255), secretQuestion varchar(255), status varchar(255), telephoneNo varchar(255), updatedAt datetime, userName varchar(30) not null, primary key (id))
create table category (id bigint not null auto_increment, createdAt datetime, name varchar(30) not null unique, status varchar(255), updatedAt datetime, primary key (id))
create table customer (id bigint not null auto_increment, address varchar(255), city varchar(255), country varchar(255), createdAt datetime, dateOfBirth date, email varchar(255) not null unique, firstName varchar(30) not null, gender varchar(255), handphoneNo varchar(255), lastName varchar(30) not null, middleName varchar(10) not null, password varchar(255), secretAnswer varchar(255), secretQuestion varchar(255), status varchar(255), telephoneNo varchar(255), updatedAt datetime, userName varchar(30) not null, primary key (id), unique (userName))
create table customer_order (id bigint not null auto_increment, amount double precision not null, date_created datetime not null, receiptURL longtext, customer_id varchar(30) not null, status_id bigint not null, primary key (id))
create table order_detail (order_id bigint not null, product_id bigint not null, qty integer not null, primary key (order_id, product_id))
create table order_status (id bigint not null auto_increment, description longtext, status varchar(255) not null, primary key (id))
create table product (id bigint not null auto_increment, createdAt datetime, description longtext not null, image longblob, name varchar(30) not null, price double precision not null, rating double precision not null, status varchar(255), updatedAt datetime, primary key (id))
create table product_category (products_id bigint not null, categories_id bigint not null)
create table product_review (product_id bigint not null, review_id bigint not null, unique (review_id))
create table review (id bigint not null auto_increment, rating double precision not null, review varchar(255) not null, primary key (id))
